.content.Intent; 5 import android.os.Bundle; 6 import android.os.Handler; 7 import android.view.Window; 8 9 public class SplashActivity extends Activity { 10 11 private final int SPLASH_DISPLAY_LENGHT = 3000; 12 private Handler handler; 13 14 @Override 15 protected void onCreate(Bundle savedInstanceState) { 16 super.onCreate(savedInstanceState); 17 getWindow().requestFeature(Window.FEATURE_NO_TITLE); 18 setContentView(R.layout.activity_splash); 19 20 handler = new Handler(); 21 // 延迟SPLASH_DISPLAY_LENGHT时间然后跳转到MainActivity 22 handler.postDelayed(new Runnable() { 23 24 @Override 25 public void run() { 26 Intent intent = new Intent(SplashActivity.this, 27 MainActivity.class); 28 startActivity(intent); 29 SplashActivity.this.finish(); 30 } 31 }, SPLASH_DISPLAY_LENGHT); 32 33 } 34 }</p>\n\n<p><a href=\"javascript:void(0);\" onclick=\"copyCnblogsCode(this)\" title=\"复制代码\"><img alt=\"复制代码\" src=\"https://common.cnblogs.com/images/copycode.gif\" /></a></p>\n\n<p>　　4、MainActivity：</p>\n\n<p><a href=\"javascript:void(0);\" onclick=\"copyCnblogsCode(this)\" title=\"复制代码\"><img alt=\"复制代码\" src=\"https://common.cnblogs.com/images/copycode.gif\" /></a></p>\n\n<p>1 package com.example.splashtest; 2 3 import android.app.Activity; 4 import android.os.Bundle; 5 import android.view.Menu; 6 import android.view.MenuItem; 7 8 public class MainActivity extends Activity { 9 10 @Override 11 protected void onCreate(Bundle savedInstanceState) { 12 super.onCreate(savedInstanceState); 13 setContentView(R.layout.activity_main); 14 } 15 }</p>\n\n<p><a href=\"javascript:void(0);\" onclick=\"copyCnblogsCode(this)\" title=\"复制代码\"><img alt=\"复制代码\" src=\"https://common.cnblogs.com/images/copycode.gif\" /></a></p>\n\n<p>　　6、修改AndroidManifest.xml文件：</p>\n\n<p><a href=\"javascript:void(0);\" onclick=\"copyCnblogsCode(this)\" title=\"复制代码\"><img alt=\"复制代码\" src=\"https://common.cnblogs.com/images/copycode.gif\" /></a></p>\n\n<p>1 　　　　 ... 2 　　　　 5 6 7 8 9 10 11 14 15 　　　　　...</p>\n\n<p><a href=\"jav